{
  "info": {
    "name": "Author Dashboard API",
    "schema": "https://schema.getpostman.com/json/collection/v2.1.0/collection.json"
  },
  "variable": [
    {
      "key": "base_url",
      "value": "http://localhost:5000/api"
    },
    {
      "key": "auth_token",
      "value": ""
    }
  ],
  "item": [
    {
      "name": "Authentication",
      "item": [
        {
          "name": "Register User",
          "request": {
            "method": "POST",
            "header": [],
            "body": {
              "mode": "raw",
              "raw": JSON.stringify({
                "name": "John Doe",
                "email": "john@example.com",
                "password": "password123",
                "phoneNumber": "+911234567890"
              }, null, 2)
            },
            "url": "{{base_url}}/auth/register"
          }
        },
        {
          "name": "Login",
          "request": {
            "method": "POST",
            "header": [],
            "body": {
              "mode": "raw",
              "raw": JSON.stringify({
                "email": "john@example.com",
                "password": "password123"
              }, null, 2)
            },
            "url": "{{base_url}}/auth/login"
          }
        }
      ]
    },
    {
      "name": "Books",
      "item": [
        {
          "name": "Create Book",
          "request": {
            "method": "POST",
            "header": [
              {
                "key": "Authorization",
                "value": "Bearer {{auth_token}}"
              }
            ],
            "body": {
              "mode": "raw",
              "raw": JSON.stringify({
                "title": "My First Book",
                "price": 19.99,
                "stock": 100,
                "category": "Fiction",
                "isbn": "978-3-16-148410-0",
                "publication": {
                  "publicationId": "PUB123",
                  "publishedDate": "2024-01-01",
                  "description": "Book description"
                }
              }, null, 2)
            },
            "url": "{{base_url}}/books"
          }
        },
        {
          "name": "Get All Books",
          "request": {
            "method": "GET",
            "header": [
              {
                "key": "Authorization",
                "value": "Bearer {{auth_token}}"
              }
            ],
            "url": "{{base_url}}/books"
          }
        }
      ]
    },
    {
      "name": "Admin",
      "item": [
        {
          "name": "Get All Users (Admin)",
          "request": {
            "method": "GET",
            "header": [
              {
                "key": "Authorization",
                "value": "Bearer {{auth_token}}"
              }
            ],
            "url": "{{base_url}}/admin/users"
          }
        },
        {
          "name": "Update Book (Admin)",
          "request": {
            "method": "PUT",
            "header": [
              {
                "key": "Authorization",
                "value": "Bearer {{auth_token}}"
              }
            ],
            "body": {
              "mode": "raw",
              "raw": JSON.stringify({
                "price": 24.99,
                "stock": 150
              }, null, 2)
            },
            "url": "{{base_url}}/admin/books/:id"
          }
        }
      ]
    },
    {
      "name": "Orders",
      "item": [
        {
          "name": "Create Order",
          "request": {
            "method": "POST",
            "header": [
              {
                "key": "Authorization",
                "value": "Bearer {{auth_token}}"
              }
            ],
            "body": {
              "mode": "raw",
              "raw": JSON.stringify({
                "bookId": "65d2a8b6c8b8f8b8f8b8f8b8",
                "quantity": 2,
                "paymentMethod": "razorpay"
              }, null, 2)
            },
            "url": "{{base_url}}/orders"
          }
        }
      ]
    },
    {
      "name": "Royalties",
      "item": [
        {
          "name": "Request Payout",
          "request": {
            "method": "POST",
            "header": [
              {
                "key": "Authorization",
                "value": "Bearer {{auth_token}}"
              }
            ],
            "body": {
              "mode": "raw",
              "raw": JSON.stringify({
                "amount": 500,
                "paymentMethod": "bank_transfer"
              }, null, 2)
            },
            "url": "{{base_url}}/royalties/request"
          }
        }
      ]
    },
    {
      "name": "Notifications",
      "item": [
        {
          "name": "Get Notifications",
          "request": {
            "method": "GET",
            "header": [
              {
                "key": "Authorization",
                "value": "Bearer {{auth_token}}"
              }
            ],
            "url": "{{base_url}}/notifications"
          }
        }
      ]
    }
  ],
  "event": [
    {
      "listen": "prerequest",
      "script": {
        "exec": [
          ""
        ],
        "type": "text/javascript"
      }
    },
    {
      "listen": "test",
      "script": {
        "exec": [
          "pm.test(\"Status code is 200\", function () {",
          "    pm.response.to.have.status(200);",
          "});"
        ],
        "type": "text/javascript"
      }
    }
  ]
}
